custom_css.gtk4:
windowcontrols image {
    background-color: transparent;
}   
/* If you want to keep the circles when you hover: */
windowcontrols image:hover {
    background-color: alpha(@headerbar_fg_color, 0.16);
}
/* When the buttons are pushed down: */
windowcontrols image:active {
    background-color: alpha(@headerbar_fg_color, 0.10);
}
headerbar windowcontrols button.close image {
    background-color: #f28c2a;
}
headerbar windowcontrols button.close image:hover {
   background-color: alpha(@headerbar_fg_color, 0.16);
}
headerbar windowcontrols button.close image:active {
    background-color: alpha(@headerbar_fg_color, 0.10);
}
headerbar:backdrop windowcontrols button.close image {
    background-color: transparent;
}
headerbar:backdrop windowcontrols button.close image:hover {
    background-color: alpha(@headerbar_fg_color, 0.10);
}
switch slider {
    background-color: #f6f6f6;
}
switch {
    background-color: #a9a9a9;
}
switch:checked {
    background-color: #ffad00;
}
checkbutton check:checked{
    color: #363636;
    background-color:#ffad00;
}
scale highlight{
    background-color: #48b9c7;
}
scale slider{
    background-color: #48b9c7;
}
list row.activatable:selected{
    color: @dark_5;
    background-color: #48b9c7;
}
window{
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
